import { useEffect, useState } from 'react';
import { EachField } from '../../Functions/util';
import './User.css';
import Loader from '../Loader';

const editableFields = ['First Name', 'Last Name'];

function BasicDetails({ userProfileData, isLoading }) {
  const [formState, setFormState] = useState({
    isEditable: false,
    formData: {},
  });

  useEffect(() => {
    setFormState((prevState) => ({
      ...prevState,
      formData: {...userProfileData},
    }));
  }, [userProfileData]);

  const handleEditField = ({ e, key, value }) => {
    setFormState((prevState) => ({
      ...prevState,
      formData: {
        ...prevState.formData,
        [key]: value,
      },
    }));
  };

  const saveDetails = () => {
    console.log(formState.formData);
  };

  const enableEdit = () => {
    setFormState(prevState => ({
      ...prevState,
      formData: userProfileData,
      isEditable: !prevState.isEditable,
    }));
  };

  return (
    <article className='flex column gap-l'>
      {
        isLoading
        ? <Loader />
        : Object.entries(formState.formData ?? {})
          .map((entry, index) => (entry &&
            <EachField
              key={index}
              {...{
                entry,
                editableFields,
                onChange: handleEditField,
                isEditable: formState.isEditable
              }} />
          ))
      }
      <br />
      <div className='flex align-center space-between'>
        <button className='edit-btn' onClick={enableEdit}>{formState.isEditable ? 'Revert' : 'Edit'}</button>
        {
          formState.isEditable &&
          <button type='submit' className='save-btn' onClick={saveDetails}>Save</button>
        }
      </div>
    </article>
  )
}

export default BasicDetails
