import { useRecoilValue } from 'recoil';
import OptionsSvg from '../../Assets/OptionsSvg';
import { authState } from '../../State/auth';
import './UserProfile.css'

function UserProfileV2() {
  const authStateData = useRecoilValue(authState);
  const { user={} } = authStateData;

  return (
    <article className='user-profile-tab justify-center flex gap-l'>
        <img className='userProfilePic' src={user?.profileUrl} />
        <div className="user-details flex flex-wrap align-center justify-between mb-2">
            <div className='flex column align-start gap-xl'>
                <h3>{user?.name}</h3>
                <email>{user?.email}</email>
            </div>
            <button className='options-menu'>
                <OptionsSvg size={35} />
            </button>
        </div>
    </article>
  )
}

export default UserProfileV2
