import React, { useEffect, useState } from "react";

const initialImages = [
  {
    img: "https://www.rri.res.in/sites/default/files/2022-09/Abhisek%20Tamang.jpg",
    name: "Image 1",
    desc: "Description for Image 1",
  },
  {
    img: "https://media.istockphoto.com/id/1386479313/photo/happy-millennial-afro-american-business-woman-posing-isolated-on-white.webp?b=1&s=170667a&w=0&k=20&c=ahypUC_KTc95VOsBkzLFZiCQ0VJwewfrSV43BOrLETM=",
    name: "Image 2",
    desc: "Description for Image 2",
  },
  {
    img: "https://media.licdn.com/dms/image/C4D03AQE0GYXvR2RNZA/profile-displayphoto-shrink_800_800/0/1662967539590?e=2147483647&v=beta&t=-8mtWCTVmAy881Kp-DdDdvGGUHCvejuFZ0jHTDSxr6w",
    name: "Image 3",
    desc: "Description for Image 3",
  },
  {
    img: "https://img.freepik.com/free-photo/indian-man-smiling-mockup-psd-cheerful-expression-closeup-portra_53876-143269.jpg?w=2000",
    name: "Image 4",
    desc: "Description for Image 4",
  },
  {
    img: "https://static2.bigstockphoto.com/7/2/4/large1500/427286867.jpg",
    name: "Image 5",
    desc: "Description for Image 5",
  },
  {
    img: "https://p1.pxfuel.com/preview/96/745/219/relaxed-indian-boy-boy.jpg",
    name: "Rajesh Kumar",
    desc: "Representing me I am Rajesh Kumar.I am very successful person because of Real Value.",
  },
  {
    img: "https://thumbs.dreamstime.com/z/young-indian-boy-portrait-cute-his-school-bag-45768444.jpg",
    name: "Muchu Tiwari",
    desc: "Hi, I'm Muchu AKA M from New York! My journey to success was greatly influenced by Real Value.",
  },
];

const Testmonials = () => {
  const [images, setImages] = useState(initialImages);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      moveItems();
    }, 3000);

    return () => clearInterval(interval);
  }, [currentIndex]);

  const moveItems = () => {
    const nextIndex = (currentIndex + 1) % images.length;
    setCurrentIndex(nextIndex);
  };

  const handleItemClick = (index) => {
    setCurrentIndex(index);
  };

  const getCircularIndex = (index, length) => {
    return (index + length) % length;
  };

  return (
    <div className="carousel-container mt-10 h-full">
      <div className="flex flex-col justify-center gap-10 text-center">
        <div className="bg-[#000000] w-screen h-32 flex align-center justify-center">
          <h1 className="text-3xl font-bold text-white">Success Stories</h1>
        </div>
        <div className="carousel-items">
          {Array(5)
            .fill()
            .map((_, index) => {
              const imageItem =
                images[
                  getCircularIndex(currentIndex + index - 2, images.length)
                ];
              return (
                <div
                  key={index}
                  className={`${index === 2 ? "sm:center" : "opacity"} ${
                    (index === 0 || index === 4) &&
                    "sm:hidden md:block lg:block"
                  } ${
                    (index === 1 || index === 3) &&
                    "sm:hidden md:block lg:block"
                  }`}
                  onClick={() =>
                    handleItemClick(
                      getCircularIndex(currentIndex + index - 2, images.length)
                    )
                  }>
                  <img
                    className="sm:h-44 h-26 mx-5 sm:w-48 w-40 rounded-md"
                    src={imageItem.img}
                    alt="item"
                  />
                </div>
              );
            })}
        </div>
        <div className="flex flex-col gap-5">
          <p>
            <b className="text-3xl font-bold text-black">
              {images[currentIndex].name}
            </b>
          </p>
          <p className="text-3xl font-thin text-black">
            {images[currentIndex].desc}
          </p>
        </div>
      </div>
    </div>
  );
};

export default Testmonials;
