export const formatKey = (str) => {
    const formattedString = str.split('_').map((word) => {
        return word.charAt(0).toUpperCase() + word.slice(1);
    }).join(' ');
    return formattedString;
};

export const EachField = ({ entry, onChange, editableFields, isEditable }) => {
  const [key, value] = entry;
  const fieldName = formatKey(key);
  const formattedValue = (key === 'initial_investment') ? `₹ ${value}` : value;
  return key ? (
      <div className='user-profile-field flex align-center gap-5xl'>
          <b>{fieldName}</b>
          <div>
            { editableFields.includes(fieldName)
            ? (
                isEditable ? (
                    <input name={key} id={key} onChange={(e) => onChange({ key, value: e.target.value })} {...{ value }} />
                ) : ( <div>{formattedValue}</div> )
            )
            : value || 'N/A' }
          </div>
      </div>
  ): null;
};
