import React, { useState } from 'react';
import '../LoginModal/Modal.css'
import { authState } from '../../State/auth';
import { useRecoilState, useRecoilValue } from 'recoil';
import { Link } from 'react-router-dom';

const SignUpForm = () => {
  const [user_id, setUserId] = useState('');
  const [OTP, setOTP] = useState('');
  const authStateData = useRecoilValue(authState);

  const handleUserId = (event) => {
    setUserId(event.target.value);
  };

  const handleOTP = (event) => {
    setOTP(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Perform signup logic here

    console.log(user_id)
    console.log(OTP)
    console.log('Sign up form submitted');
  };

  const LoginForm = () => {
    return (
      <article className='form-box'>
        <form className="form" onSubmit={handleSubmit}>
          <div >
            <div class="group">
              <input 
              required 
              type="text" 
              id='user_id' 
              name='user_id' 
              placeholder=""
              value={user_id}
              onChange={handleUserId} />
              <label for="name">User ID</label>
            </div>
            <div class="group">
              <input 
              id='OTP' 
              required 
              name='OTP' 
              value={OTP}
              placeholder=""
              type="password" 
              onChange={handleOTP} />
              <label for="name">Password</label>
            </div>
          </div>
          <button type="submit">Log In</button>
        </form>
        <div className="form-section">
          <p>Don't have an account? <a href='/signup' alt=''>Create One</a></p>
        </div>
      </article>
    )
  };

  const LogoutCard = () => {
    const [authStateData, setAuthStateData] = useRecoilState(authState);
    const { user } = authStateData;

    const handleLogout = () => {
      setAuthStateData({
        isLoggedIn: false,
        user: null,
      });
    };

    console.log({ user });

    return (
      <article className='logoutCard'>
        <header className='flex align-center gap-4xl'>
          <img className='userDP' src={user.profileUrl} alt={user.name} />
          <div className='flex column gap-l'>
            <span> {user.name} </span>
            <a href='/user'>
              <u>view profile</u>
            </a>
          </div>
        </header>
        <hr />
        <button className='logout-button' onClick={handleLogout} type="submit">Log Out</button>
      </article>
    )
  };

  return (

    <div className="flex justify-center align-center">
      { authStateData.isLoggedIn 
        ? <LogoutCard />
        : <LoginForm /> }
    </div>
  );
};

export default SignUpForm;
